function sort_info(){
	var e = $(".initials"),
		a = $("#letter");
	e.find("ul").append("<li>A</li><li>B</li><li>C</li><li>D</li><li>E</li><li>F</li><li>G</li><li>H</li><li>I</li><li>J</li><li>K</li><li>L</li><li>M</li><li>N</li><li>O</li><li>P</li><li>Q</li><li>R</li><li>S</li><li>T</li><li>U</li><li>V</li><li>W</li><li>X</li><li>Y</li><li>Z</li><li>#</li>"), initials(), $(".initials ul li").click(function() {
		var i = $(this),
			t = i.html();
		a.html(t).fadeIn(), e.css("background", "rgba(145,145,145,0.6)"), setTimeout(function() {
			e.css("background", "rgba(145,145,145,0)"), a.fadeOut()
		}, 1e3);
		var r = i.index();
		if (0 == r) $("html,body").animate({
			scrollTop: "0px"
		}, 300);
		else if (27 == r) {
			var l = $("#default").position().top;
			$("html,body").animate({
				scrollTop: l + "px"
			}, 300)
		} else {
			var s = i.text();
			if ($("#" + s).length > 0) {
				var c = $("#" + s).position().top;
				$("html,body").animate({
					scrollTop: c - 45 + "px"
				}, 300)
			}
		}
	});
	var i = $(window).height(),
		t = i - 45;
	e.height(t);
	var r = t / 36;
	e.find("li").height(r)
}